mapper.select:
SELECT * FROM ( SELECT ROWNUM AS rnum, c.* FROM ( SELECT p.place_id, p.place_thumb, p.area_code, a.area_name, p.sigungu_code, s.sigungu_name, p.place_title, p.place_cat3, cat.cat3_name, NVL((SELECT ROUND(AVG(star_rate), 1) FROM review WHERE place_id = p.place_id), 0) AS place_rating, NVL((SELECT COUNT(*) FROM review WHERE place_id = p.place_id), 0) AS place_review, (SELECT COUNT(*) FROM bookmark b WHERE b.place_id = p.place_id AND b.member_nickname = #{memberNickname}) AS bookmarked FROM place_info p LEFT JOIN area_code a ON p.area_code = a.area_code LEFT JOIN sigungu_code s ON p.sigungu_code = s.sigungu_code AND p.area_code = s.area_code LEFT JOIN place_cat3 cat ON p.place_cat3 = cat.cat3
AND p.place_id IN
#{typeId}
ORDER BY place_review DESC
ORDER BY place_rating DESC
ORDER BY bookmarked DESC
) c ) WHERE rnum BETWEEN #{pi.start} AND #{pi.end}

